import React from "react";

function ImagePopup ({card, onClose}){
    console.log(card)
    console.log(card.itemlink)
    console.log(card.name)
    return (
        <section className={`popup popup_type_image ${card.isOpen ? `popup_opened` : ""}`}>
          <div className="popup__image-box">
            <figure className="popup__image-container">
              <img className="popup__image-foto" type="url" src={card.link} alt={card.name}/>
              <figcaption className="popup__image-sign" type="text">{card.name}</figcaption>
            </figure>
            <button className="popup__close" type="button" onClick={onClose}></button>
          </div>
        </section>
    )
}

export default ImagePopup;
